    const parseCustomDate = (dateStr) =>
    {
        const [dayStr, time, period] = dateStr.split(' ');
        const daysMap = { Sun: 0, Mon: 1, Tue: 2, Wed: 3, Thu: 4, Fri: 5, Sat: 6 };
        const today = new Date();
        const targetDay = daysMap[dayStr];
        const currentDay = today.getDay();
        let dayDifference = targetDay - currentDay;
        if (dayDifference < 0) dayDifference += 7;

        let [hour, minute] = time.split(':').map(Number);
        if (period === 'PM' && hour !== 12) hour += 12;
        if (period === 'AM' && hour === 12) hour = 0;

        const resultDate = new Date(
              today.getFullYear(),
              today.getMonth(),
              today.getDate() + dayDifference,
              hour,
              minute
          );

          return resultDate.getTime();
        };

        const meteogramOptions =
        {
              chart: { type: 'column' },
              title: { text: 'Hourly Weather (Next 5 Days)' },
              xAxis: {
                  type: 'datetime',
                  labels: {
                    formatter: function () {
                    return Highcharts.dateFormat('%H', this.value); // Show time in 24-hour format
                },align: 'center',
                  },
                  tickInterval: 3600 * 1000 * 4, // Every 4 hours
              },
              yAxis: [
                  {
                      title: { text: 'Temperature (°F)' },
                      labels: { format: '{value}°F' },
                  },
                  {
                      title: { text: 'Pressure (inHg)' },
                      opposite: true,
                      labels: { format: '{value} inHg' },
                  }
              ],
              tooltip: {
                  shared: true,
                  formatter: function () {
                      const point = this.points[0].point;
                      return `<b>${Highcharts.dateFormat('%A, %b %e, %I:%M %p', point.x)}</b><br/>` +
                          `Temperature: ${point.temperature}°F<br/>` +
                          `Humidity: ${point.humidity}%<br/>` +
                          `Pressure: ${point.pressure} inHg<br/>` +
                          `Wind: ${point.windSpeed} mph`;
                  },
              },
              series: [
                  {
                      name: 'Temperature',
                      data: hourlyData.map((hour) => {
                          const parsedDate = parseCustomDate(hour.date);
                          return {
                              x: parsedDate,
                              y: hour.temperature,
                              temperature: hour.temperature,
                              humidity: hour.humidity,
                              pressure: hour.pressure,
                              windSpeed: hour.windSpeed,
                          };
                      }),
                      type: 'line',
                      color: '#FF0000'
                  },
                  {
                      name: 'Humidity',
                      data: hourlyData.map((hour) => {
                          const parsedDate = parseCustomDate(hour.date);
                          return {
                              x: parsedDate,
                              y: hour.humidity,
                          };
                      }),
                      type: 'column',
                      color: '#1E90FF',
                      yAxis: 0
                  },
                  {
                      name: 'Pressure',
                      data: hourlyData.map((hour) => {
                          const parsedDate = parseCustomDate(hour.date);
                          return {
                              x: parsedDate,
                              y: hour.pressure,
                          };
                      }),
                      type: 'spline',
                      color: '#FFA500',
                      yAxis: 1
                  },
                  {
                      name: 'Wind',
                      type: 'windbarb',
                      data: hourlyData.map((hour) => {
                          const parsedDate = parseCustomDate(hour.date);
                          return {
                              x: parsedDate,
                              value: hour.windSpeed,
                              direction: hour.windDirection,
                          };
                      }),
                      color: '#000000',
                      vectorLength: 10
                  }
        ]
    };

